fields.formula:
concat( 
  "EXP", 
  if( 
    row_id() < 10, 
    concat("0000", row_id()), 
    if( 
      row_id() < 100, 
      concat("000", row_id()), 
      if( 
        row_id() < 1000, 
        concat("00", row_id()), 
        if( 
          row_id() < 10000, 
          concat("0", row_id()), 
          row_id() 
        ) 
      ) 
    ) 
  ) 
)

if( 
  and( 
    not(isblank(lookup("Expense type", "Tax-deductible percentage"))), 
    field("Is substantiated") 
  ), 
  round( 
    multiply( 
        field("Amount"), 
        divide( 
        lookup("Expense type", "Tax-deductible percentage"), 
        100 
        ) 
    ), 
    2 
  ), 
  0
)

concat( 
  "BR", 
  if( 
    row_id() < 10, 
    concat("0000", row_id()), 
    if( 
      row_id() < 100, 
      concat("000", row_id()), 
      if( 
        row_id() < 1000, 
        concat("00", row_id()), 
        if( 
          row_id() < 10000, 
          concat("0", row_id()), 
          row_id() 
        ) 
      ) 
    ) 
  ) 
)
