fields.formula:
concat( 
  year(field("Admission time")), 
  "-", 
  if( 
    row_id() < 10, 
    concat("0000", row_id()), 
    if( 
      row_id() < 100, 
      concat("000", row_id()), 
      if( 
        row_id() < 1000, 
        concat("00", row_id()), 
        if( 
          row_id() < 10000, 
          concat("0", row_id()), 
          row_id() 
        ) 
      ) 
    ) 
  ) 
)

if(tonumber(left(field("ESI level"), 1))=5,"🩹", 
if(tonumber(left(field("ESI level"), 1))=4,"💊", 
if(tonumber(left(field("ESI level"), 1))=3,"💉", 
if(tonumber(left(field("ESI level"), 1))=2,"🩸", 
if(tonumber(left(field("ESI level"), 1))=1,"🚨", "")))))
+"  "
+totext(field("Admission time") + date_interval(field("Attention needed within")))

concat( 
  totext(year(field("Admission time"))), 
  " week ", 
  ceil( 
    divide( 
      date_diff( 
        "day", 
        todate( 
          concat( 
            totext(year(field("Admission time"))), 
            "-01-01" 
          ), 
          "YYYY-MM-DD" 
        ), 
        field("Admission time") 
      ), 
      7 
    ) 
  ) 
)

concat( 
  join( 
    datetime_format( 
        lookup("Related emergency admissions", "Admission time"), 
        "YYYYMMDD" 
    ), 
    "" 
  ), 
  "-", 
  if( 
    row_id() < 10, 
    concat("0000", row_id()), 
    if( 
      row_id() < 100, 
      concat("000", row_id()), 
      if( 
        row_id() < 1000, 
        concat("00", row_id()), 
        if( 
          row_id() < 10000, 
          concat("0", row_id()), 
          row_id() 
        ) 
      ) 
    ) 
  ) 
)
