fields.formula:
concat( 
  "  🥗 ", 
  count( 
    filter( 
      lookup("Related meal plans", "Grocery before plan"), 
      and( 
        lookup("Related meal plans", "Grocery before plan") > -1, 
        lookup("Related meal plans", "Is grocery fulfilled") = "No" 
      ) 
    ) 
  ), 
  " --:-- 🪁 ", 
  count( 
    filter( 
      lookup("Related activity plans", "Grocery before plan"), 
      and( 
        lookup("Related activity plans", "Grocery before plan") > -1, 
        lookup("Related activity plans", "Is grocery fulfilled") = "No" 
      ) 
    ) 
  ) 
)

multiply( 
  if( 
    or( 
      isblank(field("Quantity")), 
      field("Quantity") < 0.01
    ), 
    1, 
    field("Quantity") 
  ), 
  field("MSRP") 
)

add( 
  count( 
    filter( 
      lookup("Related meal plans", "Grocery before plan"), 
      and( 
        lookup("Related meal plans", "Grocery before plan") > -1, 
        lookup("Related meal plans", "Is grocery fulfilled") = "No" 
      ) 
    ) 
  ), 
  count( 
    filter( 
      lookup("Related activity plans", "Grocery before plan"), 
      and( 
        lookup("Related activity plans", "Grocery before plan") > -1, 
        lookup("Related activity plans", "Is grocery fulfilled") = "No" 
      ) 
    ) 
  ) 
)

if( 
  field("Latest plans for sorting") > 0, 
  true, 
  false 
)

add( 
  sum( 
    lookup("Related meal plans", "Estimated cost")
  ), 
  sum( 
    lookup("Related activity plans", "Estimated cost") 
  ) 
)

concat( 
  "M", 
  if( 
    row_id() < 10, 
    concat("0000", row_id()), 
    if( 
      row_id() < 100, 
      concat("000", row_id()), 
      if( 
        row_id() < 1000, 
        concat("00", row_id()), 
        if( 
          row_id() < 10000, 
          concat("0", row_id()), 
          row_id() 
        ) 
      ) 
    ) 
  ) 
)

if( 
  or( 
    isblank(field("Plan date")), 
    isblank(field("Grocery day")) 
  ), 
  "", 
  if( 
    field("Grocery before plan") < 0, 
    "😡 Missed", 
    if( 
      and( 
        field("Grocery before plan") = 0, 
        field("Is grocery fulfilled") = "No" 
      ), 
      "🛒 On grocery day", 
      if( 
        and( 
          field("Grocery before plan") > 7, 
          field("Is grocery fulfilled") = "No" 
        ), 
        "✍🏼 To do", 
        if( 
          field("Is grocery fulfilled") = "Yes", 
          "💸 Completed", 
          "📅 Timely" 
        ) 
      ) 
    ) 
  ) 
)

tonumber( 
  join( 
    totext( 
      date_diff( 
        "day", 
        field("Grocery day"), 
        field("Plan date") 
      ) 
    ), 
    "" 
  ) 
)

concat( 
  "A", 
  if( 
    row_id() < 10, 
    concat("0000", row_id()), 
    if( 
      row_id() < 100, 
      concat("000", row_id()), 
      if( 
        row_id() < 1000, 
        concat("00", row_id()), 
        if( 
          row_id() < 10000, 
          concat("0", row_id()), 
          row_id() 
        ) 
      ) 
    ) 
  ) 
)

if( 
  or( 
    isblank(field("Plan date")), 
    isblank(field("Grocery day")) 
  ), 
  "", 
  if( 
    field("Grocery before plan") < 0, 
    "😡 Missed", 
    if( 
      and( 
        field("Grocery before plan") = 0, 
        field("Is grocery fulfilled") = "No" 
      ), 
      "🛒 On grocery day", 
      if( 
        and( 
          field("Grocery before plan") > 7, 
          field("Is grocery fulfilled") = "No" 
        ), 
        "✍🏼 To do", 
        if( 
          field("Is grocery fulfilled") = "Yes", 
          "💸 Completed", 
          "📅 Timely" 
        ) 
      ) 
    ) 
  ) 
)

tonumber( 
  join( 
    totext( 
      date_diff( 
        "day", 
        field("Grocery day"), 
        field("Plan date") 
      ) 
    ), 
    "" 
  ) 
)

count( 
  filter( 
    lookup("Related grocery items", "Name"), 
    lookup("Related grocery items", "Latest plans for sorting") > 0 
  ) 
)

sum( 
  filter( 
    lookup("Related grocery items", "Total price"), 
    lookup("Related grocery items", "Latest plans for sorting") > 0 
  ) 
)
