fields.formula:
if( 
  count(field("Competitors")) < 1, 
  "", 
  concat( 
    "🤸 ", 
    count(field("Competitors")) 
  ) 
)

concat( 
  if( 
    length(concat(join(field("Business"), ""), "*")) < 2, 
    "PS", 
    left(join(field("Business"), ""), 2) 
  ), 
  if( 
    row_id() < 10, 
    concat("0000", row_id()), 
    if( 
      row_id() < 100, 
      concat("000", row_id()), 
      if( 
        row_id() < 1000, 
        concat("00", row_id()), 
        if( 
          row_id() < 10000, 
          concat("0", row_id()), 
          row_id() 
        ) 
      ) 
    ) 
  ) 
)

concat( 
  "CE", 
  if( 
    row_id() < 10, 
    concat("0000", row_id()), 
    if( 
      row_id() < 100, 
      concat("000", row_id()), 
      if( 
        row_id() < 1000, 
        concat("00", row_id()), 
        if( 
          row_id() < 10000, 
          concat("0", row_id()), 
          row_id() 
        ) 
      ) 
    ) 
  ) 
)

concat( 
  "ME", 
  if( 
    row_id() < 10, 
    concat("0000", row_id()), 
    if( 
      row_id() < 100, 
      concat("000", row_id()), 
      if( 
        row_id() < 1000, 
        concat("00", row_id()), 
        if( 
          row_id() < 10000, 
          concat("0", row_id()), 
          row_id() 
        ) 
      ) 
    ) 
  ) 
)

sum( 
  lookup("Expense estimates", "Cost") 
)

multiply( 
  multiply( 
    sum( 
      lookup("Market", "Population") 
    ), 
    divide( 
      field("Estimated turnout (%)"), 
      100 
    ) 
  ), 
  avg( 
    lookup("Main products/services", "Price")
  ) 
)

concat( 
  "🛎 ", 
  floor( 
    divide( 
      multiply( 
        sum( 
          lookup("Market", "Population") 
        ), 
        divide( 
          field("Estimated turnout (%)"), 
          100 
        ) 
      ), 
      lookup("Business", "Avg days per month") 
    ) 
  ) 
)

concat( 
  "F", 
  if( 
    row_id() < 10, 
    concat("0000", row_id()), 
    if( 
      row_id() < 100, 
      concat("000", row_id()), 
      if( 
        row_id() < 1000, 
        concat("00", row_id()), 
        if( 
          row_id() < 10000, 
          concat("0", row_id()), 
          row_id() 
        ) 
      ) 
    ) 
  ) 
)

if( 
  or( 
    isblank(field("Capital cost")), 
    isblank(field("Avg monthly profit")) 
  ), 
  "", 
  concat( 
    "💵 ", 
    ceil( 
      multiply( 
        divide( 
          tonumber(field("Avg monthly profit")), 
          tonumber(field("Capital cost")) 
        ), 
        100
      ) 
    ), 
    "%"
  ) 
)

if( 
  count(field("Initial costs")) < 1, 
  "", 
  sum( 
    lookup("Initial costs", "Cost") 
  ) 
)

if( 
  count(field("Monthly estimates")) < 1, 
  "", 
  round( 
    avg( 
      lookup("Monthly estimates", "Estimated profit") 
    ), 
    2 
  ) 
)

if( 
  count(field("Monthly estimates")) < 1, 
  "", 
  round( 
    avg( 
      lookup("Monthly estimates", "Estimated expenses") 
    ), 
    2 
  ) 
)

if( 
  count(field("Monthly estimates")) < 1, 
  "", 
  round( 
    avg( 
      lookup("Monthly estimates", "Estimated sales") 
    ), 
    2 
  ) 
)

if( 
  or( 
    isblank(field("Capital cost")), 
    isblank(field("Avg monthly profit")) 
  ), 
  "", 
  concat( 
    "💰 ", 
    round( 
      divide( 
        tonumber(field("Capital cost")), 
        multiply( 
          tonumber(field("Avg monthly profit")), 
          12 
        ) 
      ), 
      2 
    ), 
    " year(s)"
  ) 
)
